function checkName(){var e=document.getElementById("name").value;""==e?document.getElementById("nameSpan").innerHTML="姓名不能为空":e.length<3?document.getElementById("nameSpan").innerHTML="姓名不能少于4位":e.length<10?document.getElementById("nameSpan").innerHTML="√":document.getElementById("nameSpan").innerHTML="名字太长"}function checkPhone(){var e=document.getElementById("phoneNum").value,n=/^1\d{10}$/;n.test(e)?document.getElementById("phoneNumSpan").innerHTML="√":""==e?document.getElementById("phoneNumSpan").innerHTML="手机号不能为空":document.getElementById("phoneNumSpan").innerHTML="×"}function checkEmail(){var e=document.getElementById("email").value,n=/^([a-zA-Z0-9_-])+@([a-zA-Z0-9_-])+(.[a-zA-Z0-9_-])+/;n.test(e)?document.getElementById("emailSpan").innerHTML="√":""==e?document.getElementById("emailSpan").innerHTML="邮箱不能为空":document.getElementById("emailSpan").innerHTML="×"}